import React, { useState } from "react";
import axios from "axios";
import PdfViewer from "./components/PdfViewer";
import './App.css';

export default function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [numPages, setNumPages] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedFile) {
      alert("Please select a PDF file.");
      return;
    }

    const formData = new FormData();
    formData.append("pdfFile", selectedFile);

    console.log(formData);

    try {
      const response = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      console.log(response.data); // Log the response from the backend

      const numPages = response.data.numPages;
      console.log(response.data.numPages);
      // Handle the response and display the visual representation of all pages
      // and enable page selection as needed

    } catch (error) {
      alert("Error uploading the file:", error);
      // Handle errors, show error messages, etc.
    }
  };

  return (
    <div className="main">
      <h1>Extract Pages from your PDF!</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input type="file" className="form-control-file" name="pdfFile" onChange={handleFileChange} />
          <br />
          <input type="submit" value="Submit PDF" className="btn btn-default" />
        </div>
      </form>
      {numPages !== null && <PdfViewer numPages={numPages} />}
    </div>
  );
}
