import React, { useState, useEffect } from "react";
import axios from "axios";
import { PDFDocument } from "pdf-lib";
import './App.css';

export default function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [selectedPages, setSelectedPages] = useState([]);
  const [pageImages, setPageImages] = useState([]);
  const [loading, setLoading] = useState(false); // Add a loading state

  useEffect(() => {
    // Fetch the number of pages and generate page images from the selectedFile
    if (selectedFile) {
      const reader = new FileReader();

      reader.onload = async () => {
        const pdfBuffer = reader.result;
        const pdfDoc = await PDFDocument.load(pdfBuffer);
        const numPages = pdfDoc.getPageCount();
        console.log("Frontend Page no: " + numPages);
        setNumPages(numPages);

        // Generate an array of image URLs for each page
        const images = await Promise.all(
          Array.from({ length: numPages }, async (_, index) => {
            if (selectedFile !== reader.result) {
              // If the selectedFile has changed before the PDFDocument is loaded,
              // cancel the ongoing operation to avoid potential errors
              return null;
            }
            const page = await pdfDoc.getPage(index + 1);
            const pngImage = await page.renderAsPNG();
            const dataUrl = URL.createObjectURL(new Blob([pngImage], { type: "image/png" }));
            return dataUrl;
          })
        );

        // Filter out null values (occurred due to cleanup)
        setPageImages(images.filter((image) => image !== null));
        setLoading(false); // Set loading state to false
      };

      setLoading(true); // Set loading state to true
      reader.readAsArrayBuffer(selectedFile);

      // Cleanup function to cancel the ongoing operation
      return () => {
        reader.abort();
      };
    }
  }, [selectedFile]);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleCheckboxChange = (event, page) => {
    const isChecked = event.target.checked;
    const pageNumber = page;

    setSelectedPages((prevSelectedPages) =>
      isChecked
        ? [...prevSelectedPages, pageNumber]
        : prevSelectedPages.filter((p) => p !== pageNumber)
    );
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedFile) {
      alert("Please select a PDF file.");
      return;
    }

    setLoading(true); // Set loading state to true

    const formData = new FormData();
    formData.append("pdfFile", selectedFile);

    // Send selectedPages separately as a JSON object
    const selectedPagesData = {
      selectedPages: selectedPages,
    };

    try {
      const response = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
        params: selectedPagesData, // Pass selectedPagesData as query params
        responseType: 'blob', // Tell axios to treat the response as a binary blob
      });

      console.log(response.data); // Log the response from the backend

      // Trigger download by creating a Blob and using a link to initiate the download
      const blob = new Blob([response.data], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'modified.pdf');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

    } catch (error) {
      alert("Error uploading the file:", error);
      // Handle errors, show error messages, etc.
    } finally {
      setLoading(false); // Set loading state to false
    }
  };

  return (
    <div className="main">
      <h1>Extract Pages from your PDF!</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input type="file" className="form-control-file" accept=".pdf" name="pdfFile" onChange={handleFileChange} />
          <br />
          {loading ? (
            <p>Loading...</p>
          ) : numPages !== null && pageImages.length === numPages ? (
            <div>
              <p>Select Pages to Include:</p>
              {Array.from({ length: numPages }, (_, index) => {
                const pageNumber = index + 1;
                const isChecked = selectedPages.includes(pageNumber);

                return (
                  <label key={index}>
                    <img src={pageImages[index]} alt={`Page ${pageNumber}`} width="150" height="150" />
                    <br />
                    <input
                      type="checkbox"
                      className="form-checkbox"
                      name="selectCheckbox"
                      checked={isChecked}
                      onChange={(event) => handleCheckboxChange(event, pageNumber)}
                    />
                    Page {pageNumber}
                  </label>
                );
              })}
              <br /> <br />
            </div>
          ) : null}
          <input type="submit" value="Download Modified PDF" className="btn btn-default" />
        </div>
      </form>
    </div>
  );
}
