import React, { useState, useEffect } from "react";
import axios from "axios";

const PdfViewer = ({ numPages, pdfBase64 }) => {
    const [selectedPages, setSelectedPages] = useState([]);
    const [imageUrls, setImageUrls] = useState([]);

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(() => {
        // Fetch images for all pages when component mounts
        fetchPageImages();
    }, []);

    const fetchPageImages = async () => {
        const urls = [];

        for (let i = 0; i < numPages; i++) {
            try {
                const response = await axios.post(
                    "http://localhost:5000/extract",
                    { filename: "your-pdf-filename.pdf", selectedPages: [i + 1] },
                    { responseType: "arraybuffer" }
                );

                const imageBase64 = Buffer.from(response.data, "binary").toString(
                    "base64"
                );
                urls.push(`data:image/png;base64,${imageBase64}`);
            } catch (error) {
                console.error("Error fetching page image:", error);
            }
        }

        setImageUrls(urls);
    };

    const handlePageSelection = (pageNumber) => {
        if (selectedPages.includes(pageNumber)) {
            setSelectedPages((prevSelected) =>
                prevSelected.filter((page) => page !== pageNumber)
            );
        } else {
            setSelectedPages((prevSelected) => [...prevSelected, pageNumber]);
        }
    };

    return (
        <div className="pdf-viewer">
            <h2>Number of Pages: {numPages}</h2>
            {imageUrls.map((imageUrl, index) => (
                <div key={index}>
                    <img src={imageUrl} alt={`Page ${index + 1}`} />
                    <label>
                        <input
                            type="checkbox"
                            checked={selectedPages.includes(index + 1)}
                            onChange={() => handlePageSelection(index + 1)}
                        />
                        Page {index + 1}
                    </label>
                </div>
            ))}
            <div>
                <button onClick={() => console.log(selectedPages)}>
                    Extract Pages
                </button>
            </div>
        </div>
    );
};

export default PdfViewer;
