import React, { useEffect, useState } from "react";
import axios from "axios";

const PdfViewer = ({ numPages, pdfBase64, pdfImageUrls }) => {
  const [selectedPages, setSelectedPages] = useState([]);
  const [images, setImages] = useState([]); // Add images state

  const handlePageSelection = (pageNumber) => {
    if (selectedPages.includes(pageNumber)) {
      setSelectedPages((prevSelected) =>
        prevSelected.filter((page) => page !== pageNumber)
      );
    } else {
      setSelectedPages((prevSelected) => [...prevSelected, pageNumber]);
    }
  };

  useEffect(() => {
    fetchPageImages();
  }, []);

  const fetchPageImages = async () => {
    try {
      const imagePromises = pdfImageUrls.map((url) =>
        axios.get(url, { responseType: "blob" })
      );
      const imageResponses = await Promise.all(imagePromises);
      const images = imageResponses.map((response) => {
        const blobUrl = URL.createObjectURL(response.data);
        return blobUrl;
      });
      setImages(images);
    } catch (error) {
      console.error("Error fetching page images:", error);
    }
  };

  return (
    <div className="pdf-viewer">
      <h2>Number of Pages: {numPages}</h2>
      {images.map((image, index) => (
        <div key={index}>
          <img src={image} alt={`Page ${index + 1}`} />
          <label>
            <input
              type="checkbox"
              checked={selectedPages.includes(index + 1)}
              onChange={() => handlePageSelection(index + 1)}
            />
            Page {index + 1}
          </label>
        </div>
      ))}
      <div>
        <button onClick={() => console.log(selectedPages)}>Extract Pages</button>
      </div>
    </div>
  );
};

export default PdfViewer;
