import React, { useState, useEffect } from "react";
import { PDFDocument } from "pdf-lib";
import pdfjs from "pdfjs-dist";
import "./App.css";

export default function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [selectedPages, setSelectedPages] = useState([]);
  const [pageImages, setPageImages] = useState([]);

  useEffect(() => {
    // Fetch the number of pages from the selectedFile without uploading it to the backend
    if (selectedFile) {
      const reader = new FileReader();

      reader.onload = async () => {
        const pdfBuffer = reader.result;
        const pdfDoc = await PDFDocument.load(pdfBuffer);
        const numPages = pdfDoc.getPageCount();
        console.log("Frontend Page no: " + numPages);
        setNumPages(numPages);
      };

      reader.readAsArrayBuffer(selectedFile);
    }
  }, [selectedFile]);

  useEffect(() => {
    // Load page images if numPages is available
    if (numPages) {
      convertPDFtoImages();
    }
  }, [numPages]);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleCheckboxChange = (event, page) => {
    const isChecked = event.target.checked;
    const pageNumber = page;

    setSelectedPages((prevSelectedPages) =>
      isChecked
        ? [...prevSelectedPages, pageNumber]
        : prevSelectedPages.filter((p) => p !== pageNumber)
    );
  };

  const convertPDFtoImages = async () => {
    const loadingTask = pdfjs.getDocument(new Uint8Array(selectedFile));
    const pdf = await loadingTask.promise;
    const images = [];

    for (let i = 1; i <= pdf.numPages; i++) {
      const page = await pdf.getPage(i);
      const viewport = page.getViewport({ scale: 1.0 });

      const canvas = document.createElement("canvas");
      const context = canvas.getContext("2d");
      canvas.width = viewport.width;
      canvas.height = viewport.height;

      const renderContext = {
        canvasContext: context,
        viewport: viewport,
      };

      await page.render(renderContext).promise;

      // Convert canvas to image URL
      const imageUrl = canvas.toDataURL("image/png");
      images.push(imageUrl);
    }

    setPageImages(images);
  };

  return (
    <div className="main">
      <h1>Extract Pages from your PDF!</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="file"
            className="form-control-file"
            accept=".pdf"
            name="pdfFile"
            onChange={handleFileChange}
          />
          <br />
          {numPages !== null && (
            <div>
              <p>Select Pages to Include:</p>
              {Array.from({ length: numPages }, (_, index) => (
                <div key={index}>
                  <label>
                    Page {index + 1}
                    <input
                      type="checkbox"
                      className="form-checkbox"
                      name="selectCheckbox"
                      checked={selectedPages.includes(index + 1)}
                      onChange={(event) => handleCheckboxChange(event, index + 1)}
                    />
                  </label>
                  {pageImages.length > 0 && (
                    <img
                      src={pageImages[index]}
                      alt={`Page ${index + 1}`}
                      style={{ maxWidth: "200px" }}
                    />
                  )}
                </div>
              ))}
              <br /> <br />
            </div>
          )}
          <input type="submit" value="Download Modified PDF" className="btn btn-default" />
        </div>
      </form>
    </div>
  );
}
