import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { PDFDocument, PDFJS } from "pdf-lib"; // Update the import statement
import "./App.css";

export default function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [selectedPages, setSelectedPages] = useState([]);
  const [pageImages, setPageImages] = useState([]);

  useEffect(() => {
    // Fetch the number of pages from the selectedFile without uploading it to the backend
    if (selectedFile) {
      const reader = new FileReader();

      reader.onload = async () => {
        const pdfBuffer = reader.result;
        const pdfDoc = await PDFDocument.load(pdfBuffer);
        const numPages = pdfDoc.getPageCount();
        console.log("Frontend Page no: " + numPages);
        setNumPages(numPages);
      };

      reader.readAsArrayBuffer(selectedFile);
    }
  }, [selectedFile]);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleCheckboxChange = (event, page) => {
    const isChecked = event.target.checked;
    const pageNumber = page;

    setSelectedPages((prevSelectedPages) =>
      isChecked
        ? [...prevSelectedPages, pageNumber]
        : prevSelectedPages.filter((p) => p !== pageNumber)
    );
  };

  const convertPDFtoImages = useCallback(async () => {
    PDFJS.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${PDFJS.version}/pdf.worker.js`; // Set workerSrc for pdfjs
    const loadingTask = PDFJS.getDocument(new Uint8Array(selectedFile)); // Use PDFJS to load the PDF
    const pdf = await loadingTask.promise;
    const images = [];

    for (let i = 1; i <= pdf.numPages; i++) {
      const page = await pdf.getPage(i);
      const viewport = page.getViewport({ scale: 1.0 });

      const canvas = document.createElement("canvas");
      const context = canvas.getContext("2d");
      canvas.width = viewport.width;
      canvas.height = viewport.height;

      const renderContext = {
        canvasContext: context,
        viewport: viewport,
      };

      await page.render(renderContext).promise;

      // Convert canvas to image URL
      const imageUrl = canvas.toDataURL("image/png");
      images.push(imageUrl);
    }

    setPageImages(images);
  }, [selectedFile]);

  useEffect(() => {
    // Load page images if numPages is available
    if (numPages) {
      convertPDFtoImages();
    }
  }, [numPages, convertPDFtoImages]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedFile) {
      alert("Please select a PDF file.");
      return;
    }

    const formData = new FormData();
    formData.append("pdfFile", selectedFile);

    // Send selectedPages separately as a JSON object
    const selectedPagesData = {
      selectedPages: selectedPages,
    };

    try {
      const response = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
        params: selectedPagesData, // Pass selectedPagesData as query params
        responseType: 'blob', // Tell axios to treat the response as a binary blob
      });

      console.log(response.data); // Log the response from the backend

      // Trigger download by creating a Blob and using a link to initiate the download
      const blob = new Blob([response.data], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'modified.pdf');
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

    } catch (error) {
      alert("Error uploading the file:", error);
      // Handle errors, show error messages, etc.
    }
    setSelectedFile(null);
    setNumPages(null);
    setSelectedPages([]);
  };

  return (
    <div className="main">
      <h1>Extract Pages from your PDF!</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="file"
            className="form-control-file"
            accept=".pdf"
            name="pdfFile"
            onChange={handleFileChange}
          />
          <br />
          {numPages !== null && (
            <div>
              <p>Select Pages to Include:</p>
              {Array.from({ length: numPages }, (_, index) => (
                <div key={index}>
                  <label>
                    Page {index + 1}
                    <input
                      type="checkbox"
                      className="form-checkbox"
                      name="selectCheckbox"
                      checked={selectedPages.includes(index + 1)}
                      onChange={(event) => handleCheckboxChange(event, index + 1)}
                    />
                  </label>
                  {pageImages.length > 0 && (
                    <img
                      src={pageImages[index]}
                      alt={`Page ${index + 1}`}
                      style={{ maxWidth: "200px" }}
                    />
                  )}
                </div>
              ))}
              <br /> <br />
            </div>
          )}
          <input type="submit" value="Download Modified PDF" className="btn btn-default" />
        </div>
      </form>
    </div>
  );
}
