import React, { useState, useEffect } from "react";
import { Document, Page } from "react-pdf";
import axios from "axios";

const PdfViewer = ({ numPages, pdfBase64 }) => {
    const [selectedPages, setSelectedPages] = useState([]);
    const [pdfFile, setPdfFile] = useState(null);
    const [pdfUrl, setPdfUrl] = useState("");

    useEffect(() => {
        const fetchPdf = async () => {
            try {
                const response = await axios.post(
                    "http://localhost:5000/pdf/complete",
                    { pdfBase64 },
                    {
                        headers: {
                            "Content-Type": "application/json",
                        },
                        responseType: "blob",
                    }
                );

                const blob = new Blob([response.data], { type: "application/pdf" });
                const url = URL.createObjectURL(blob);
                setPdfFile(blob);
                setPdfUrl(url);
            } catch (error) {
                console.error("Error fetching the PDF:", error);
            }
        };

        fetchPdf();
    }, [pdfBase64]);

    const handlePageSelection = (pageNumber) => {
        setSelectedPages((prevSelected) =>
            prevSelected.includes(pageNumber)
                ? prevSelected.filter((page) => page !== pageNumber)
                : [...prevSelected, pageNumber]
        );
    };

    const handleExtractPages = async () => {
        if (selectedPages.length === 0) {
            alert("Please select at least one page to extract.");
            return;
        }

        const formData = new FormData();
        formData.append("pdfFile", pdfFile);
        formData.append("selectedPages", JSON.stringify(selectedPages));

        try {
            const response = await axios.post("http://localhost:5000/extract-pages", formData, {
                headers: { "Content-Type": "multipart/form-data" },
                responseType: "blob",
            });

            const blob = new Blob([response.data], { type: "application/pdf" });
            const url = URL.createObjectURL(blob);
            window.open(url);
        } catch (error) {
            console.error("Error extracting pages:", error);
        }
    };

    return (
        <div className="pdf-viewer">
            <h2>Number of Pages: {numPages}</h2>
            <Document file={pdfUrl}>
                {Array.from({ length: numPages }, (_, i) => (
                    <div key={i}>
                        <Page pageNumber={i + 1} />
                        <label>
                            <input
                                type="checkbox"
                                checked={selectedPages.includes(i + 1)}
                                onChange={() => handlePageSelection(i + 1)}
                            />
                            Page {i + 1}
                        </label>
                    </div>
                ))}
            </Document>
            <div>
                <button onClick={handleExtractPages}>Extract Pages</button>
            </div>
        </div>
    );
};

export default PdfViewer;
