import React, { useState, useEffect } from "react";
import axios from "axios";
import './App.css';

export default function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [selectedPages, setSelectedPages] = useState([]);

  useEffect(() => {
    // Fetch the number of pages from the backend once the file is selected
    if (selectedFile) {
      const formData = new FormData();
      formData.append("pdfFile", selectedFile);

      axios.post("http://localhost:5000/getNumPages", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      })
        .then(response => {
          const numPages = response.data.numPages;
          setNumPages(numPages);
        })
        .catch(error => {
          console.error("Error fetching numPages:", error);
        });
    }
  }, [selectedFile]);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleCheckboxChange = (event, page) => {
    const isChecked = event.target.checked;
    setSelectedPages((prevSelectedPages) =>
      isChecked
        ? [...prevSelectedPages, page]
        : prevSelectedPages.filter((p) => p !== page)
    );
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedFile) {
      alert("Please select a PDF file.");
      return;
    }

    const formData = new FormData();
    formData.append("pdfFile", selectedFile);
    formData.append("selectedPages", JSON.stringify(selectedPages));

    try {
      const response = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      console.log(response.data); // Log the response from the backend

      // Handle the response and display the visual representation of all pages
      // and enable page selection as needed

    } catch (error) {
      alert("Error uploading the file:", error);
      // Handle errors, show error messages, etc.
    }
  };

  return (
    <div className="main">
      <h1>Extract Pages from your PDF!</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input type="file" className="form-control-file" name="pdfFile" onChange={handleFileChange} />
          <br />
          {numPages !== null && (
            <div>
              <p>Select Pages to Include:</p>
              {Array.from({ length: numPages }, (_, index) => (
                <label key={index}>
                  Page {index + 1}
                  <input
                    type="checkbox"
                    className="form-checkbox"
                    name="selectCheckbox"
                    checked={selectedPages.includes(index + 1)}
                    onChange={(event) => handleCheckboxChange(event, index + 1)}
                  />
                </label>
              ))}
            </div>
          )}
          <input type="submit" value="Submit PDF" className="btn btn-default" />
        </div>
      </form>
    </div>
  );
}
