import React, { useState, useEffect } from "react";
import axios from "axios";

const PdfViewer = ({ numPages, images }) => {

    const [selectedPages, setSelectedPages] = useState([]);

    const handlePageSelection = (pageNumber) => {
        if (selectedPages.includes(pageNumber)) {
            setSelectedPages((prevSelected) =>
                prevSelected.filter((page) => page !== pageNumber)
            );
        } else {
            setSelectedPages((prevSelected) => [...prevSelected, pageNumber]);
        }
    };

    // Function to fetch individual page images
    const fetchPageImage = async (pageNumber) => {
        try {
            const response = await axios.get(`http://localhost:5000/page/${pageNumber}`, {
                responseType: 'arraybuffer',
            });

            // Convert the received image data (base64 string) to a data URL
            const imageDataUrl = `data:image/png;base64,${Buffer.from(response.data).toString('base64')}`;

            // Add the image data URL to the images array
            setImages((prevImages) => [...prevImages, imageDataUrl]);
        } catch (error) {
            console.error('Error fetching page image:', error);
        }
    };

    // Function to fetch page images when the component mounts
    useEffect(() => {
        const fetchImages = async () => {
            for (let pageNumber = 1; pageNumber <= numPages; pageNumber++) {
                await fetchPageImage(pageNumber);
            }
        };

        fetchImages();
    }, [numPages]);

    return (
        <div className="pdf-viewer">
            <h2>Number of Pages: {numPages}</h2>
            {images.map((image, index) => (
                <div key={index}>
                    <img src={image} alt={`Page ${index + 1}`} />
                    <label>
                        <input
                            type="checkbox"
                            checked={selectedPages.includes(index + 1)}
                            onChange={() => handlePageSelection(index + 1)}
                        />
                        Page {index + 1}
                    </label>
                </div>
            ))}
            <div>
                <button onClick={() => console.log(selectedPages)}>Extract Pages</button>
            </div>
        </div>
    );
};

export default PdfViewer;
